var i = 0;

function deleteComments() {
    var X = 20
    if (i > X) {
        $(".message-" + (i - (X + 1))).remove();
    }
}

function randomComment() {
    var color = colors[Math.floor(Math.random() * colors.length)]
    var name = names[Math.floor(Math.random() * names.length)]
    var comment = comments[Math.floor(Math.random() * comments.length)]
    $(".chat").append('<div class="message message-' + i + '" style="display: none;"><user class="' + color + '">' + name + ':</user> ' + comment + '<hr class="bg-' + color + '"></div>')
    $(".message-" + i).fadeIn();
    $(".chat").scrollTop($(".chat")[0].scrollHeight);
    i += 1;
    u_comments = false;
    $(".comments").text(parseInt($(".comments").text()) + 1);
}

function aComment() {
    var message = $("#user-message").val();
    if (message.length > 0) {
        $("#user-message").removeClass("i-error");
        $(".chat").append('<div class="message message-' + i + '" style="display: none;"><user class="white">Anonymous:</user> ' + message + '<hr class="bg-white"></div>')
        $(".message-" + i).fadeIn();
        $(".chat").scrollTop($(".chat")[0].scrollHeight);
        $("#user-message").val("");
        i += 1;
        u_comments = false;
        $(".comments").text(parseInt($(".comments").text()) + 1);
    } else {
        $("#user-message").addClass("i-error");
    }
}

function like() {
    if (!voted) {
        $(".likes").text(parseInt($(".likes").text()) + 1);
        voted = true;
        $(".fa-thumbs-down").css("opacity", 0.75);
        $(".dislikes").css("opacity", 0.75);
        $(".fa-thumbs-down").css("pointer-events", "none");
        $(".fa-thumbs-up").css("color", "#2ecc40");
        $(".likes").css("color", "#2ecc40");
    }
}

function dislike() {
    if (!voted) {
        $(".dislikes").text(parseInt($(".dislikes").text()) + 1);
        voted = true;
        $(".fa-thumbs-up").css("opacity", 0.75);
        $(".likes").css("opacity", 0.75);
        $(".fa-thumbs-up").css("pointer-events", "none");
        $(".fa-thumbs-down").css("color", "#ff4136");
        $(".dislikes").css("color", "#ff4136");
    }
}

$("#user-message").keypress(function(e) {
    if(e.which == 13) {
        aComment();
    }
});

var s_bonus = 0;

var l_bonus = 0;

var d_bonus = 0;

function updateStatistics() {
    
    var s_date = new Date();
    
    s_bonus += Math.floor(Math.random() * 11) - 5;
    
    l_bonus += Math.floor(Math.random() * 4);
    
    d_bonus += Math.floor(Math.random() * 2);
        
    var online = Math.round((s_date.getSeconds() + (60 * (s_date.getMinutes() + (60 * s_date.getHours())))) / 50 + s_bonus + 2000);
    
    var comments = Math.round((s_date.getSeconds() + (60 * (s_date.getMinutes() + (60 * s_date.getHours())))) / 1000 + 100)
    
    var likes = Math.round((s_date.getSeconds() + (60 * (s_date.getMinutes() + (60 * s_date.getHours())))) / 250 + l_bonus + 100);
    
    var dislikes = Math.round((s_date.getSeconds() + (60 * (s_date.getMinutes() + (60 * s_date.getHours())))) / 1500 + d_bonus + 15);
    
    $(".likes").text(likes);

    $(".dislikes").text(dislikes);
    
    $(".online").text(online);
    
    if (u_comments) {
        $(".comments").text(comments)
    }
    
}

function play() {
    
    $(".s-1").fadeOut("slow", function() {
        $(".s-2").fadeIn("slow", function() {
            setTimeout(function() {
                $(".load").text("Looking for closest server...");
                setTimeout(function() {
                    $(".load").text("Connecting...");
                    setTimeout(function() {
                        $(".load").html("All servers are full. You need <strong>FREE VERIFY</strong> status to watch the <strong>UFC 232 stream</strong> for free.");
                        $(".load").addClass("red");
                        setTimeout(function() {
                            $(".fa-spin").fadeOut("slow", function() {
                                $(".verify").fadeIn("slow");
                            });
                        }, 3000);
                    }, 4000);
                }, 2000);
            }, 2000);
        });
    });
    
}

function fixchat() {
    if ($(window).width() < 768) {
        $(".chat-column").css("min-height", $(".PLAYER").height())
        $(".chat-column").css("margin-top", "1rem");
    };
;}

$(window).on("resize", function(){
    fixchat();
});

fixchat();

updateStatistics();
randomComment();

